sqlGroup.sql:
 
	FROM (
		SELECT IFNULL(SUM(wc.money), 0) ktsr,IFNULL(SUM(wc.tMoney), 0) sysr,
		(
			SELECT IFNULL(SUM(dm.money), 0) FROM draw_money dm WHERE dm.did=wc.id
		)ytsr,
		(
			SELECT IFNULL(SUM(wi.money), 0) FROM wx_income wi WHERE wi.type=1 AND wi.did=wc.id
		)swsr,wc.openid as openId
		FROM wx_client wc WHERE  wc.openid=#'$openId$'#
		
	)t
